CREATE TABLE public.dict_bolota (
    name text, -- Название болота	
    reg text, -- Район	
    nom int, -- Номер по кадастру	
    mat text, -- Материалы разведки	
    bind text, -- Привязка	
    s_all DECIMAL, -- Площадь общая	
    s_pm DECIMAL, -- Площадь пром. залежи 	
    p_max DECIMAL, -- Мощность торфяного пласта, макс, м	
    p_avg DECIMAL, -- Мощность торфяного пласта, сред., м	
    zapas_kubm DECIMAL, -- Запас торфа, тыс. куб.м	
    zapas_tt DECIMAL, -- Запас торфа, тыс.т	
    tip text --Тип и качеств. хар-ка торфяной залеж
);

